mapper.update:
update SB02 
set text = #{text}
where seq = #{seq}
and seq01 = #{seq01}

update SB02 
set good = (select good from SB02 where seq = #{seq}) + 1
where seq = #{seq}

update SB02 
set good = (select good from SB02 where seq = #{seq}) - 1
where seq = #{seq}

mapper.delete:
delete from SB02
where seq = #{seq}
and seq01 = #{seq01}

delete from SB02
where seq01 = #{seq01}
and parents = #{parents}

delete from SB02
where seq01 = #{seq01}
and parents = #{parents}
