mapper.select:
select * from (select ceil(row_number() over(order by logintime desc) / #{rowPerPage} ) as nowPage, userid as userid, logintime as logintime, browser as browser, device as device, ip as ip, count(*) over() as totCnt from log_login where 1 = 1
and userid like #{userid} || '%'
and browser = #{browser}
and device = #{device}
) where nowPage = #{nowPage}

mapper.insert:
insert into log_chatting
	(
	username, logdate, message

	, chatroom

	)
	values (#{username}, #{logdate}, #{message}

	, #{chatroom}

	)
