mapper.select:
select S1.userid as userid, S1.username as username, S1.brth as brth, S1.email as email, S1.regdate as regdate, S1.grade as grade, S.pfnum as pfnum, S.pcnum as pcnum, S.pbnum as pbnum from (select T1.* from SMI1 T1 where T1.pbnum = #{cellpbnum}
and T1.pcnum = #{cellpcnum}
and T1.pfnum = #{cellpfnum}
AND T1.infocode = 1 and not exists ( /*+ UNNEST NL_AJ */ select 1 from SME1 E1 where T1.userid = E1.userid ) ) S inner join SM01 S1 on S.userid = S1.userid
