mapper.update:
update SV04 
set text = #{text}
where seq = #{seq}
and seq01 = #{seq01}

update SV04 
set good = (select good from SV04 where seq = #{seq}) + 1
where seq = #{seq}

update SV04 
set good = (select good from SV04 where seq = #{seq}) - 1
where seq = #{seq}

mapper.delete:
delete from SV04
where seq = #{seq}
and seq01 = #{seq01}

delete from SV04
where seq01 = #{seq01}
and parents = #{parents}
